import { fetcher, poster } from "../../utils/axios"

export const getUserNonce = async (account: string) => {
  const res = await fetcher(`/api/user/nonce/${account}`)

  return res
}

export const airdropV2 = async (account: string, signature: string) => {
  console.log(account, signature, 2222222222)
  const res = await poster(`/api/user/airdropV2`, { account, signature})

  return res
}
