import React from "react"
import styled from 'styled-components'

const Text = styled.span`
  color: #000;
  font-family: Inter;
`

export const TextGray = styled(Text)<{
  color?: string
}>`
  color: ${({ color }) => color || 'rgba(0,0,0,0.30)'};
`

export const TextBlue = styled(Text)<{
  color?: string
}>`
  color: ${({ color }) => color || '#49BCFF'};
  font-size: 16px;
  font-style: normal;
  font-weight: 600;
  line-height: normal;

`

export const TextGrad = styled(Text)<{
  fontSize?: string,
  fontWeight?: string
}>`
  font-size: ${({ fontSize }) => fontSize || '20px'};
  font-style: normal;
  font-weight: ${({ fontWeight }) => fontWeight || '600'};;
  line-height: normal;
  background: var(--airdrop, linear-gradient(135deg, #3F3CFF 6.8%, #6BBEE1 55.97%, #8AE899 101.2%));
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`



export default Text
