import { fetcher, poster } from "../../utils/axios"

export const getUserNonce = async (account: string) => {
  const res = await fetcher(`/api/user/nonce/${account}`)

  return res
}

export const airdropV2 = async (account: string, sign: string) => {
  console.log(account, sign, 2222222222)
  const res = await poster(`/api/airdrop/airdropV2`, { account, sign})

  return res
}
